      <div class="profile__headers" @click="getAdvts(user.id)">
        <h2
          class="profile__header profile__header-main"
          @click="tab('profile')"
          :class="{ 'header-active': header === 'profile' }"
        >
          Профиль
        </h2>
        <h2
          v-if="userId == user.id"
          class="profile__header profile__header-new"
          @click="tab('new')"
          :class="{ 'header-active': header === 'new' }"
        >
          Новое объявление/мероприятие
        </h2>
        <h2
          v-if="roleUser === 'admin'"
          class="profile__header profile__header-admin"
        >
          <span @click="openBansWindow()">Админ-панель</span>
          <div class="bans-window" :class="{ 'bans-window-open': bansIsOpen }">
            <IssuingBansComponent
              @click="output(user.id)"
              :rests="{
                comment: Number(rest_comment),
                advt: Number(rest_advt),
                car: Number(rest_car),
                part: Number(rest_part),
                event: Number(rest_event),
                message: Number(rest_message),
              }"
              :id="user.id"
              :userId="userId"
            />
          </div>
        </h2>
      </div>
