<script>
import axios from "axios";
import HeaderComponent from "../components/HeaderComponent.vue";
import ShowAdvtComponent from "@/components/ShowAdvtComponent.vue";
import BansComponent from "@/components/BansComponent.vue";
import {
  encodeText,
  setCookie,
  decodeText,
  getCookie,
} from "../views/EventsView.vue";
import FormsInProfileComponent from "@/components/FormsInProfileComponent.vue";
import IssuingBansComponent from "@/components/IssuingBansComponent.vue";

export default {
  data() {
    return {
      encodeText,
      setCookie,
      decodeText,
      getCookie,
      justRating: 0,
      totalRating: 0.0,
      user: {},
      roleUser: "",
      rest_comment: "",
      rest_advt: "",
      rest_car: "",
      rest_part: "",
      rest_event: "",
      rest_message: "",
      boat: 'Не имею',
      motor_boat: 'Не имею',
      tent: 'Не имею',
      fishing_rod: 'Не имею',
      countDays: 0,
      events: 0,
      parts: 0,
      comments: 0,
      advts: [],
      estimates: 0,
      userId: this.$route.params.id,
      advtId: Number,
      openList: "My",
      myRating: null,
      header: "profile",
      bansIsOpen: false,
      inputIsDisabled: true,
      dateIsDisabled: false,
      inventoryIsDisabled: true,
    };
  },
  components: {
    HeaderComponent,
    ShowAdvtComponent,
    BansComponent,
    FormsInProfileComponent,
    IssuingBansComponent,
  },
  mounted() {
    this.output(this.userId);
  },
  methods: {
    output(id) {
      axios
        .post("http://cz57780.tw1.ru/api/getUser", {
          id: id,
        })
        .then((response) => {
          let res = response.data;
          this.user = res.user[0];
          this.getAdvts(this.user.id);
          this.roleUser = this.user.role;
          setCookie('role', this.user.role);
          this.rest_comment = this.user.rest_comment;
          this.rest_advt = this.user.rest_advt;
          this.rest_car = this.user.rest_car;
          this.rest_event = this.user.rest_event;
          this.rest_part = this.user.rest_participate_in_the_event;
          this.rest_message = this.user.rest_message;
          this.userId = getCookie("id");
          this.events = res.events;
          this.parts = res.parts;
          this.comments = res.comments;
          this.estimates = res.estimates;
          this.fio = this.user.name + " " + this.user.surname;
          this.date = this.user.date_of_birth;
          if (this.user.boat == null) this.boat = 'Не имею';
          else this.boat = this.user.boat;
          if (this.user.motor_boat == null) this.motor_boat = 'Не имею';
          else this.motor_boat = this.user.motor_boat;
          if (this.user.tent == null) this.tent = 'Не имею';
          else this.tent = this.user.tent;
          if (this.user.fishing_rod == null) this.fishing_rod = 'Не имею';
          else this.fishing_rod = this.user.fishing_rod;
          this.totalRating = 0;
          for (let i = 0; i < this.user.rating.length; i++) {
            this.totalRating = this.totalRating + this.user.rating[i].rating;
          }
          this.justRating = this.user.rating.length;
          if (this.user.rating.length !== 0) {
            this.totalRating = this.totalRating / this.user.rating.length;
          } else {
            this.totalRacing = "Нет оценок";
          }
          this.totalRating = this.totalRating.toFixed(1);
          this.getCountDaysInPortal();
          this.getRating();
          // this.date = new Date(Date.parse(this.user.created_at));
          // this.dateTime = this.date.toLocaleTimeString().slice(0, -3);
          // this.dateDay = this.date.toLocaleDateString();
          // this.user.created_at = this.dateTime + " " + this.dateDay;
        })
        .catch((error) => {
          console.log(error);
        });
    },
    logout() {
      let cookies = document.cookie.split(";");
      for (let i = 0; i < cookies.length; i++) {
        let cookie = cookies[i];
        let eqPos = cookie.indexOf("=");
        let name = eqPos > -1 ? cookie.substr(0, eqPos) : cookie;
        document.cookie = name + "=;expires=Thu, 01 Jan 1970 00:00:00 GMT;";
        document.cookie =
          name + "=; path=/; expires=Thu, 01 Jan 1970 00:00:01 GMT;";
      }
      this.$router.push("/advertisements");
    },
    fill(count) {
      // console.log(count);
      let stars = document.querySelectorAll(".profile__star");
      for (let index = 0; index < stars.length; index++) {
        stars[index].style.fill = "none";
      }
      if (count !== null) {
        for (let index = 0; index < count; index++) {
          stars[index].style.fill = "#192A39";
        }
      }
    },
    getCountDaysInPortal() {
      this.countDays = Math.round(
        (new Date() - Date.parse(this.user.created_at)) / 1000 / 60 / 60 / 24
      );
    },
    getAdvts(id) {
      axios
        .get(`http://cz57780.tw1.ru/api/advt/${id}`)
        .then((res) => {
          // console.log(res);
          this.advts = res.data;
        })
        .catch((error) => {
          console.log(error);
        });
    },
    slider(direction, length) {
      let list = document.querySelector(".advts__list");
      let listStyles = window.getComputedStyle(list);
      let max = 480 * (length - 3);
      switch (direction) {
        case "prev":
          if (parseInt(listStyles.left) < 0)
            list.style.left = `${parseInt(listStyles.left) + 480}px`;
          break;
        case "next":
          if (parseInt(listStyles.left) > -max)
            list.style.left = `${parseInt(listStyles.left) - 480}px`;
          break;
      }
    },
    toggleAdvt() {
      document.querySelector(".advt__window").classList.toggle("advt-open");
    },
    showAdvt(id) {
      this.advtId = id;
      this.toggleAdvt();
    },
    bansOpen() {
      document.querySelector(".bans__window").classList.toggle("advt-open");
    },
    getRating() {
      if (this.userId != this.user.id)
        axios
          .post("http://cz57780.tw1.ru/api/getRating", {
            id: this.userId,
            user_for_id: this.user.id,
          })
          .then((response) => {
            this.myRating = response.data[0].rating;
            this.fill(this.myRating);
          })
          .catch((error) => {
            console.log(error);
          });
    },
    tab(text) {
      this.header = text;
    },
    openBansWindow() {
      this.bansIsOpen === false
        ? (this.bansIsOpen = true)
        : (this.bansIsOpen = false);
    },
    addRating(rating) {
      axios
        .post("http://cz57780.tw1.ru/api/rating", {
          user_who_id: this.userId,
          user_for_id: this.user.id,
          rating: rating,
        })
        .then((res) => {
          console.log(res);
          this.myRating = rating;
          this.fill(this.myRating);
          this.output(this.user.id);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    editPhoto() {
      console.log(this.$refs.photo.files[0]);
      let formData = new FormData();
      formData.append("photo", this.$refs.photo.files[0]);
      formData.append("id", this.userId);
      axios
        .post("http://cz57780.tw1.ru/api/editPhoto", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        })
        .then((res) => {
          console.log(res);
          this.output(this.userId);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    enabledInput(boolean) {
      this.inputIsDisabled = boolean;
    },
    enabledInventory(boolean) {
      this.inventoryIsDisabled = boolean;
    },
    editInfo(boolean) {
      axios
        .post("http://cz57780.tw1.ru/api/updateUser", {
          id: this.userId,
          name: this.fio.split(" ")[0],
          surname: this.fio.split(" ")[1],
          // date_of_birth: this.date_of_birth
        })
        .then((res) => {
          console.log(res);
          this.enabledInput(boolean);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    editDate() {
      axios
        .post("http://cz57780.tw1.ru/api/updateDateUser", {
          id: this.userId,
          date_of_birth: this.date,
        })
        .then((res) => {
          console.log(res);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    editInventary(boolean) {
      axios
        .post("http://cz57780.tw1.ru/api/updateInventory", {
          id: this.userId,
          boat: this.boat,
          motor_boat: this.motor_boat,
          tent: this.tent,
          fishing_rod: this.fishing_rod,
        })
        .then((res) => {
          console.log(res);
          this.enabledInventory(boolean);
        })
        .catch((error) => {
          console.log(error);
        });
    },
  },
};
</script>

